class DBHelper() {

    // below is the method for creating a database by a sqlite query
    fun onCreate(db: SQLiteDatabase) {
        // below is a sqlite query, where column names
        // along with their data types is given
        val query = "CREATE TABLE Users(uid TEXT PRIMARY KEY, username TEXT, password TEXT)"
        db.execSQL(query)

        val query2 = "CREATE TABLE Recipes(id INTEGER PRIMARY KEY, name TEXT, ingredients TEXT, tag TEXT, rating INTEGER, photo BLOB, creator TEXT REFERENCES Users(uid))"
        db.execSQL(query2)
        mockData(db)
    }

    fun onUpgrade(db: SQLiteDatabase, p1: Int, p2: Int) {
        // this method is to check if table already exists
        db.execSQL("DROP TABLE IF EXISTS Users")
        db.execSQL("DROP TABLE IF EXISTS Recipes")
        onCreate(db)
    }

    // This method is for adding data in our database
    fun addUser(db: SQLiteDatabase, username : String, password : String ){
        val query = "INSERT INTO Users(uid, username, password) VALUES(" + UUID.randomUUID().toString() + ", " + username + ", " + password + ")"
        db.execSQL(query)

        db.close()
    }

    fun addRecipe(db: SQLiteDatabase, name : String, ingredients : String, tag : String, rating: Number, photo: Blob, creator: String) {
        // below we are creating
        // a content values variable
        val values = ContentValues()
        val query = "INSERT INTO Recipes (id, name, tag, rating, photo, creator) VALUES(" +
                UUID.randomUUID().toString() + ", " + name + ", " + ingredients + ", " + tag + ", " + rating.toInt() +
                ", " + photo + ", " + creator + ");"
        db.execSQL(query)
        db.close()
    }

    // below method is to get
    // all recipes from the database
    fun getRecipes(db: SQLiteDatabase) {
        return db.execSQL("SELECT * FROM Recipes;", null)
    }

